package Basics;

public class SwitchCase {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		
		
		String studentname ="java";
		
		switch(studentname){
			
			
		case "manual":
			System.out.println("kumar raj");
			break;
			
			
		case "Selinium":
			System.out.println("naresh kumar");
			break;
			
			
		case "java":
			System.out.println("naresh java");
			break;
			
			
		case "SQL":
			System.out.println("ruhu sql");
			break;
			
			default:
				System.out.println("not found");
				break;
			
			
		}

	}

}
